OST['owner'];	$sql = ' WHERE OWNER LIKE \'%'.$name1.'%\' AND OWNER LIKE \'%'.$name2.'%\' AND OWNER LIKE \'%'.$name3.'%\' ORDER BY OWNER ASC';}if (isset($_POST['parcel_submitted']) OR isset($_POST['address_submitted']) OR isset($_POST['owner_submitted'])) {		// Query the db	$query = "SELECT * FROM properties".$sql."";	$result = @mysql_query ($query);	$num = mysql_num_rows ($result);	// Set the row colors and start the row count at 0	$even = 'even'; 	$odd = 'odd'; 	$row_count = 0;	// If it ran okay, display the records	if ($result) {		// If there were no records found		if (!$num) 	{			echo '<p class="results">We\'re sorry, your search for <strong>' . $search_by . '</strong> returned no results. Please try again.</p>';		} else {			$plural = ($num == 1) ? 'result' : 'results';			echo '<p><p class="results">Your search for <strong>' . $search_by . '</strong> returned <strong>' . $num . '</strong> ' . $plural . '.</p>';?>	<table id="property_results"><thead><tr><th width="100">Parcel</th><th width="260" class="center">Address</th><th width="260" class="center">Owner</th><th width="100" class="center">Zone</th></tr></thead><tbody><?php			// Loop through the records			while ($row = mysql_fetch_array ($result, MYSQL_ASSOC)) {							$row_color = ($row_count % 2) ? $even : $odd;				// Determine if county zoning				if (empty($row['ZONE']) OR $row['ZONE'] == '                 ') {					$zone = '<span class="county">County</span>';				} else 	{					$zone = $row['ZONE'];				}				echo '<tr class="' . $row_color . '"><td class="center">' . $row['PARCEL'] . '</td><td class="center">' . $row['ADDRESS'] . '</td><td class="center">' . $row['OWNER'] . '</td><td class="center">' . $zone . '</td></tr>';				// Add 1 to every row				$row_count++;			} // End while loop		} // End else?>		</tbody></table><?php	mysql_free_result($result); // Free up the resources	} else { // If it did not run okay		echo '<p class="error">The records could not be displayed due to a system error. We apologize for any inconvenience.</p><p>'.mysql_error().'</p>';	}} else { // End if isset	echo '<p class="center">Please search for property information using the form above.</p>';}mysql_close(); // Close the db connection?>			</div>		<?php endwhile; endif; ?>	<?php edit_post_link('Edit this entry.', '<p>', '</p>'); ?>	</div>		</main><!-- #main -->	</div><!-- #primary --><?php get_sidebar(); ?><?php get_footer(); ?>